public class HackerRank {
    public static void main(String[] args) {
        System.out.println(hackerrankInString(""));
    }
    static String hackerrankInString(String str) {
        char[] verification = {'h','a','c','k','e','r','r','a','n','k'};
        int j = 0;
        String isPresent = "YES";
        for (int i = 0; i < str.length(); i++) {
            char c = str.charAt(i);
            if(verification[j] == c){
                j++;
            }
            if(verification.length == j){
                break;
            }
        }
        if(verification.length != j){
            isPresent = "NO";
        }
        return isPresent;
    }

}
